    }
  }));

  const classes = useStyles();

  return (
    <div className={classes.root}>
      <div className={clsx(classes.containText, 'width-adjust')}>
        <h1 className={classes.titleBanner}>Hi there ðŸ‘‹, is Andres Parra here..!</h1>
      </div>
    </div>
  )
}

